INSERT INTO phrases (language, page, section, key, phrase) VALUES
('bg', 'general_education', 'header', 'title', 'Общообразователна подготовка - НТГ'),
('bg', 'general_education', 'banner', 'title', 'Общообразователна подготовка'),
('bg', 'general_education', 'banner', 'subtitle', 'Качествено образование за бъдещите лидери в икономиката и бизнеса'),
('bg', 'general_education', 'intro', 'title', 'Общообразователната подготовка в Национална търговска гимназия'),
('bg', 'general_education', 'intro', 'text1', 'Общообразователната подготовка в Национална търговска гимназия, гр.Пловдив се осъществява на базата на Наредба №5 от 30.11.2025 г. на МОН, в която са определени държавните образователни стандарти и структурата на учебните програми по общообразователните предмети.'),
('bg', 'general_education', 'intro', 'text2', 'Ключовите компетентности са взаимозависими и представляват съвкупност от знания, умения и отношения, необходими за личностното развитие на индивида през целия живот, за изграждането на активна гражданска позиция и участие в социалния живот, както и за пригодността му за реализация на пазара на труда.'),
('bg', 'general_education', 'features', 'title', 'Нашите предимства'),
('bg', 'general_education', 'features', 'subtitle', 'Защо да изберете нашата общообразователна подготовка'),
